function getCropName(){
	if(nbt['cropName']=='unknown'){return "未知"}
	if(nbt['cropName']=='weed'){return "杂草"}
	if(nbt['cropName']=='wheat'){return "小麦"}
	if(nbt['cropName']=='pumpkin'){return "南瓜"}
	if(nbt['cropName']=='melon'){return "西瓜"}
